import React,{useState} from "react";
import DeleteIcon from '@mui/icons-material/Delete';
import AddIcon from '@mui/icons-material/Add';
import "./Todolist.css"

export default function Todolist()
{
    const [item,setitem] = useState("")
    const [arr,setarr] = useState([])

    const typing=(e)=>{
        const x = e.target.value
        setitem(x)

    }
    const placed=()=>{    
            
            if (item!==""){
            setarr([...arr,item])
            
        }
        setitem("")
}


// const clicked = () => {

//     if (temp !== "") {
//         setarr((pre) => {
//            return  [...pre, temp]
//     })
//     }
//     settemp("")
// }

 const del = (ind)=>{

            const update = arr.filter((ele,i)=>{
                return ind!==i})
            setarr(update)           
 }

 const delall=()=>{
    setarr([])
 }
    return(
    <>
    <div className="container">

        <div className="maincon">

        <h3 className="text-capitalize ">todolist</h3>
        <input placeholder="✍️ Add items" value={item} onChange={typing}></input>
        {/* <button className="btn btn-outline-success btn-sm" onClick={placed}>add</button> */}
        <AddIcon onClick={placed}/>

       { arr.map((e,i)=>(
        <>
        <div >
        
        <li >{e} <DeleteIcon fontSize="sm" onClick={()=>{del(i)}}/> </li>
        </div>
        </>
        ))}
        <br/>
        <button className=" btn btn-outline-danger btn-sm" style={{marginTop:"10px"}} onClick={delall}>Remove all</button>
        </div>
        </div>
    </>)
}
