import React,{useState} from "react";

export default function Todolist()
{
    const [item,setitem] = useState("")

    const typing=(e)=>{
        const x = e.target.value
        setitem(x)
    }
    return(
    <>
        <h3>todolist</h3>
        <input placeholder="✍️ Add items" value={item} onChange={(e)=>{setitem(e.target)}}></input>
        <h2>{item}</h2>

    </>)
}
