import React,{useState} from "react";
import DeleteIcon from '@mui/icons-material/Delete';
import "./Todolist.css"

export default function Todolist()
{
    const [item,setitem] = useState("")
    const [arr,setarr] = useState([])

    const typing=(e)=>{
        const x = e.target.value
        setitem(x)

    }
    const placed=()=>{    
            <>
            {setarr([...arr,item])}
            {setitem("")}
            </>
}

 const del = (ind)=>{

            const update = arr.filter((ele,i)=>{
                return ind!==i})
            setarr(update)           
 }

 const delall=()=>{
    setarr([])
 }
    return(
    <>
        <h3 className="Text-Capitalization">todolist</h3>
        <input placeholder="✍️ Add items" value={item} onChange={typing}></input>
        <button onClick={placed}>add</button>

       { arr.map((e,i)=>(
        <>
        <div >
        
        <li >{e} <DeleteIcon fontSize="sm" onClick={()=>{del(i)}}/> </li>
        </div>
        </>
        ))}
        <br/>
        <button className="btn btn-outline-danger btn-sm" style={{marginTop:"10px"}} onClick={delall}>Remove all</button>
    </>)
}
